targets.rawSql:
select now() as time, count(*) as num_subgraphs
   from (
   select d.id, d.latest_ethereum_block_number block
     from subgraphs.subgraph_deployment d,
          subgraphs.subgraph_deployment_assignment a
    where d.id = a.id
    and not d.failed) a
   where a.block < 100;
